let is_click_buy_now = false;
let is_reload_buy_now = false;
function buy_now() {
    console.log('buy_now');
    const buy_now_selector = 'button[class="btn btn-solid-primary btn--l YuENex eFAm_w"]:not(.btn-solid-primary--disabled)';
    const buy_now_button_list = $(buy_now_selector);
    if (buy_now_button_list.length >= 1 && !is_click_buy_now) {
        const buy_now_button = buy_now_button_list.first();
        console.log('buy_now click', buy_now_button);
        is_click_buy_now = true;
        setTimeout(() => {
            buy_now_button.click();
        }, 500);
    }

    const cannot_buy_selector = 'button[class="btn btn-solid-primary btn--l btn-solid-primary--disabled YuENex eFAm_w"]';
    const cannot_buy_button_list = $(cannot_buy_selector);
    if (cannot_buy_button_list.length >= 1 && !is_reload_buy_now) {
        is_reload_buy_now = true;
        location.reload();
    }
}

function check_out() {
    console.log('check_out');
