        // place_order_button.click();
    }, 330);
}

setInterval(() => {
    const currentLocation = window.location;
    const currentUrl = currentLocation.href;
    if (currentUrl.includes('https://shopee.tw/product') || currentUrl.includes('https://shopee.tw/POP')) {
        buy_now();
    } else if (currentUrl.includes('https://shopee.tw/cart')) {
        check_out();
    } else if (currentUrl.includes('https://shopee.tw/checkout')) {
        place_order();
    }
}, 350);
